import { useEffect, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import restaurantService from "../restaurant.service";
import { Link } from "react-router-dom";

const RestaurantList = () => {

    const [restaurants, setRestaurants] = useState([]);

    useEffect(() => {
      init();
    }, []);

    const init = () => {
        restaurantService.getAll()
        .then(response => {
            console.log('Printing the restaurants data', response.data);
            setRestaurants(response.data);
        })
        .catch(error => {
            console.log('Something went wrong', error);
        })
    }

    const handleDelete = id => {
        restaurantService.remove(id)
            .then(response => {
                console.log('Restaurant deleted successfully', response.data);
                init();
            })
            .catch(error => {
                console.log('Something went wrong', error);
              })
    }

    return ( 
        <div className="container">
            <h3>List of Restaurants</h3>
            <hr/>
            <div>
                <Link to="/add" className="btn btn-primary mb-2">Add Restaurant</Link>
                <table className="table table-bordered table-striped">
                <thead className="thead-dark">
                    <tr>
                    <th>Name</th>
                    <th>Location Description</th>
                    <th>Address</th>
                    <th>Food Items</th>
                    <th>Schedule</th>
                    <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    restaurants.map(restaurant => (
                        <tr key={restaurant.id}>
                        <td>{restaurant.name}</td>
                        <td>{restaurant.locationDescription}</td>
                        <td>{restaurant.address}</td>
                        <td>{restaurant.foodItems}</td>
                        <td><a href={restaurant.schedule}>Schedule</a></td>
                        <td>
                            <Link className="btn btn-info" to={`/restaurants/edit/${restaurant.id}`}>Update</Link>
                            <button className="btn btn-danger" onClick={(e) => handleDelete(restaurant.id)}>Delete</button>
                        </td>
                        </tr>
                    ))
                    }
                </tbody>
                </table>
            </div>
        </div>
     );
}
 
export default RestaurantList;
