import { useEffect, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import restaurantService from "../restaurant.service";
import { Link } from "react-router-dom";

const RestaurantList = () => {

    const [restaurants, setRestaurants] = useState([]);

    useEffect(() => {
      init();
    }, []);

    const init = () => {
        restaurantService.getAll()
        .then(response => {
            console.log('Printing the restaurants data', response.data);
            setRestaurants(response.data);
        })
        .catch(error => {
            console.log('Something went wrong', error);
        })
    }

    // const handleDelete = id => {
    //     restaurantService.remove(id)
    //         .then(response => {
    //             console.log('Restaurant deleted successfully', response.data);
    //             init();
    //         })
    //         .catch(error => {
    //             console.log('Something went wrong', error);
    //           })
    // }

    return ( 
        <div className="container">
            <h3>List of Restaurants</h3>
            <hr/>
            <div>
                <Link to="/add" className="btn btn-primary mb-2">Add Restaurant</Link>
            </div>
            <Row xs={1} md={2} className="g-4">
                {Array.from({ length: 4 }).map((_, idx) => (
                    <Col>
                    <Card>
                        <Card.Img variant="top" src="holder.js/100px160" />
                        <Card.Body>
                        <Card.Title>Card title</Card.Title>
                        <Card.Text>
                            This is a longer card with supporting text below as a natural
                            lead-in to additional content. This content is a little bit longer.
                        </Card.Text>
                        </Card.Body>
                    </Card>
                    </Col>
                ))}
            </Row>
                {
                restaurants.map(restaurant => (
                    <div class="card" key={restaurant.id}>
                    <div class="card-body">
                        <h5 class="card-title">{restaurant.name}</h5>
                        <p class="card-text">{restaurant.address}</p>
                    </div>
                    </div>
                ))
                }
            
        </div>
     );
}
 
export default RestaurantList;
