import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from "react-router-dom";

const RestaurantDetails = (props) => {

    const [restaurants, setRestaurants] = useState([]);

    useEffect(() => {
        init();
      }, []);
  
      const init = () => {
          restaurantService.getAll()
          .then(response => {
              console.log('Printing the restaurants data', response.data);
              setRestaurants(response.data);
          })
          .catch(error => {
              console.log('Something went wrong', error);
          })
      }

    const viewDetails = id => {
        restaurantService.get(id)
            .then(response => {
                console.log('Restaurant details retrieved successfully', response.data);
                init();
            })
            .catch(error => {
                console.log('Something went wrong', error);
              })
    }

    return (
        <div className="container">
        <div className="card text-center" key={props.id}>
            <div className="card-body">
                <h6 className="card-title">{props.name}</h6>
                <p className="card-text"></p>
                <ul className="list-group list-group-flush">
                    <li className="list-group-item">{props.food}</li>
                </ul>
            </div>
        </div>
        <Link to="/">Back to List</Link>
        </div>
    );
}
 
export default RestaurantDetails;
